const MainBody = React.forwardRef(({ gradient, title, message, icons }, ref) => {
  return (
    <div
      fluid
      id="home"
      style={{
        background: `linear-gradient(136deg,${gradient})`,
        backgroundSize: "1200% 1200%",
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
      className="bg-transparent text-light d-flex align-items-center flex-wrap m-0"
    >
      <Container className="text-center">
        <h1 ref={ref} className="display-1">{title}</h1>
        <Typist>
          <div className="lead">{message}</div>
        </Typist>
        <div className="p-5">
          {icons.map((icon, index) => (
            <a
              key={`social-icon-${index}`}
              target="_blank"
              rel="noopener noreferrer"
              href={icon.url}
              aria-label={`My ${icon.image.split("-")[1]}`}
            >
              <i className={`fab ${icon.image} fa-3x`} />
            </a>
          ))}
        </div>
        <a
          className="btn btn-outline-light btn-lg"
          href="#aboutme"
          role="button"
          aria-label="Learn more about me"
        >
          More about me
        </a>
      </Container>
    </div>
  );
});
