import React, { useState } from "react";
import { useScrollPosition } from "../hooks/useScrollPosition";
import useResizeObserver from "../hooks/useResizeObserver";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import "./Navbar.scss"
// import { mainBody, repos, about, skills } from "../editable-stuff/config.js";
import { NavLink } from "../containers/greeting/migration";

const Navigation = React.forwardRef((props, ref) => {
  // const { showBlog, FirstName } = config;
  const [isTop, setIsTop] = useState(true);
  const [scrollPosition, setScrollPosition] = useState(0);
  const navbarMenuRef = React.useRef();
  const navbarDimensions = useResizeObserver(navbarMenuRef);
  const navBottom = navbarDimensions ? navbarDimensions.bottom : 0;
  const [scrolled, setScrolled] = useState(false);

  const handleScroll = () => {
    const offset = window.scrollY;
    if (offset > 200) { // Set the scroll offset as desired
      setScrolled(true);
    } else {
      setScrolled(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  let navbarClasses = ['navbar']; // Your original navbar class
  if (scrolled) {
    navbarClasses.push('navbar-scroll');
  }
  // useScrollPosition(
  //   ({ prevPos, currPos }) => {
  //     if (!navbarDimensions || !ref.current) return;
  //     currPos.y + ref.current.offsetTop - navbarDimensions.bottom > 5
  //       ? setIsTop(true)
  //       : setIsTop(false);
  //     setScrollPosition(currPos.y);
  //   },
  //   [navBottom]
  // );

  // React.useEffect(() => {
  //   if (!navbarDimensions) return;
  //   navBottom - scrollPosition >= ref.current.offsetTop
  //     ? setIsTop(false)
  //     : setIsTop(true);
  // }, [navBottom, navbarDimensions, ref, scrollPosition]);

  return (
    <Navbar
      ref={navbarMenuRef}
      className={`px-3 fixed-top  ${!isTop ? "navbar-white" : "navbar-transparent"
        }`}
      expand="lg"
    >
      <Navbar.Brand className="navbar-brand" href={process.env.PUBLIC_URL + "/#home"}>
        {/* {`<${mainBody.firstName} />`} */}
      </Navbar.Brand>
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="navbar-nav mr-auto">
          {
            <NavLink className="nav-item lead">
              {/* <Link to={process.env.PUBLIC_URL + "/blog"}>Blog</Link> */}
            </NavLink>
          }
          {(

            <NavLink
              href={process.env.PUBLIC_URL + "/#projects"}
            >
              Projects
            </NavLink>
          )}
          <NavLink
            className="nav-item lead"
            // href={about.resume}
            target="_blank"
            rel="noreferrer noopener"
          >
            Resume
          </NavLink>
          {(
            <NavLink
              className="nav-item lead"
              href={process.env.PUBLIC_URL + "/#aboutme"}
            >
              About
            </NavLink>
          )}
          {(
            <NavLink
              className="nav-item lead"
              href={process.env.PUBLIC_URL + "/#skills"}
            >
              Skills
            </NavLink>
          )}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
});

export default Navigation;
