const Greeting = React.forwardRef((props, ref) => {
  const { isDark } = useContext(StyleContext);

  if (!greeting.displayGreeting) {
    return null;
  }

  // Assuming your 'greeting' object has a 'gradient' field like the 'MainBody' props
  const gradient = greeting.gradient || ['#06beb6', '#48b1bf']; // default gradient

  return (
    <Fade bottom duration={1000} distance="40px">
      <Jumbotron
        fluid
        id="greeting"
        style={{
          background: `linear-gradient(136deg,${gradient.join(',')})`,
          backgroundSize: "1200% 1200%",
        }}
        className="bg-transparent text-light min-vh-100 d-flex align-content-center align-items-center flex-wrap m-0"
      >
        <Container className="text-center">
          <h1 ref={ref} className="display-1">
            {greeting.title}
          </h1>
          <Typist cursor={{ show: false }}>
            <span className="lead typist">{greeting.subTitle}</span>
          </Typist>
          <div className="social-media-icons p-5">
            {/* Example social icons rendering */}
            {greeting.socialLinks.map((icon, index) => (
              <a
                key={`social-icon-${index}`}
                target="_blank"
                rel="noopener noreferrer"
                href={icon.url}
                aria-label={`My ${icon.label}`}
              >
                <i className={`fab ${icon.image} fa-3x`} />
              </a>
            ))}
          </div>
          <a
            className="btn btn-outline-light btn-lg"
            href="#about"
            role="button"
            aria-label="Learn more about me"
          >
            More about me
          </a>
        </Container>
      </Jumbotron>
    </Fade>
  );
});

export default Greeting;
