const Greeting = React.forwardRef((props, ref) => {
  const { isDark } = useContext(StyleContext);

  if (!greeting.displayGreeting) {
    return null;
  }

  return (
    <Fade bottom duration={1000} distance="40px">
      <Jumbotron
        fluid
        id="greeting"
        style={{
          background: `linear-gradient(136deg, ${isDark ? '#333' : '#fff'}, ${isDark ? '#666' : '#eee'})`,
          backgroundSize: "1200% 1200%",
        }}
        className="bg-transparent text-light min-vh-100 d-flex align-content-center align-items-center flex-wrap m-0"
      >
        <Container className="text-center">
          <h1 ref={ref} className="display-1">
            {greeting.title} <span className="wave-emoji">{emoji("ðŸ‘‹")}</span>
          </h1>
          <Typist>
            <div className="lead typist">
              {greeting.subTitle}
            </div>
          </Typist>
          <div className="p-5">
            <SocialMedia />
            <Button text="Contact me" href="#contact" />
            {greeting.resumeLink && (
              <Button
                text="See my resume"
                newTab={true}
                href={greeting.resumeLink}
              />
            )}
          </div>
          {illustration.animated && (
            <img
              alt="person at the desk"
              src={require("../../assets/images/manOnTable.svg")}
              className="mt-5"
            />
          )}
        </Container>
      </Jumbotron>
    </Fade>
  );
});

export default Greeting;
